
M= "Ciao come va?"

m_int= int(M.encode("utf-8").hex(), 16)
n=""
n_int= int(n, 16)
e= 65537

c_int= pow(m_int, e, n_int )
print(c_int)

d = ""

d_int= int(d, 16)

dec= pow(c_int, d_int, n_int)

lunghezza = (n_int.bit_length() + 7) // 8
m_bytes = dec.to_bytes(lunghezza, "big").lstrip(b"\x00")
M_dec = m_bytes.decode("utf-8")

print(M_dec)


